import React, { useState, useEffect } from 'react';
import * as Stomp from 'stompjs';

const RabbitMQComponent = () => {
    const [stompClient, setStompClient] = useState(null);
    const [isConnected, setIsConnected] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Cleanup on component unmount
        return () => {
            if (stompClient && isConnected) {
                stompClient.disconnect(() => {
                    console.log("Disconnected");
                });
            }
        };
    }, [stompClient, isConnected]);

    const connectAndSendMessage = () => {
        const sendMsg = () => {
            if (stompClient) {
                stompClient.send("/queue/frontendQueue", {}, JSON.stringify({ message: message }));
                console.log("Message sent: ", message);
            }
        };

        if (!isConnected) {
            const socket = new WebSocket('ws://192.168.192.1:15674/ws'); // Use your RabbitMQ Web-STOMP endpoint
            const client = Stomp.over(socket);

            const headers = {
                login: "admin", // use secure methods for production
                passcode: "admin", // use secure methods for production
            };

            client.connect(headers, frame => {
                console.log('Connected: ' + frame);
                setStompClient(client);
                setIsConnected(true);
                sendMsg(); // Send the message after connection is established
            }, error => {
                console.error('Error connecting to RabbitMQ:', error);
            });
        } else {
            sendMsg(); // If already connected, just send the message
        }
    };

    return (
        <div>
            <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
            <button onClick={connectAndSendMessage}>Send Message</button>
        </div>
    );
};

export default RabbitMQComponent;
