import React, { useState, useEffect } from 'react';
import * as Stomp from 'stompjs';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
const [message, setMessage] = useState('');
const [receivedMessages, setReceivedMessages] = useState([]); // Store received messages

const RabbitMQComponent = () => {
    const [stompClient, setStompClient] = useState(null);
    const [isConnected, setIsConnected] = useState(false);
    const navigate = useNavigate(); // Initialize navigate for redirection

    useEffect(() => {
        const connect = () => {
            const socket = new WebSocket('ws://192.168.192.211:15674/ws');
            const client = Stomp.over(socket);

            const headers = {
                login: "admin",
                passcode: "admin",
            };

            client.connect(headers, frame => {
                console.log('Connected: ' + frame);
                setStompClient(client);
                setIsConnected(true);

                client.subscribe('/queue/backendQueue', (message) => {
                    console.log("Received message: ", message.body);
                    if (message.body === 'True') {
                        console.log('SUCCESS');
                        navigate('/dashboard'); // Redirect to dashboard upon successful message receipt
                    } else {
                        console.log("SORRY");
                    }
                    setReceivedMessages(prevMessages => [...prevMessages, message.body]);
                });
            }, error => {
                console.error('Error connecting to RabbitMQ:', error);
            });
        };

        connect();

        return () => {
            if (stompClient && isConnected) {
                stompClient.disconnect(() => {
                    console.log("Disconnected from RabbitMQ");
                    setIsConnected(false);
                });
            }
        };
    }, [navigate]); // Add navigate to the dependency array

    return <div>RabbitMQ Connection Established</div>;
};

export default RabbitMQComponent;
