import React, { useState } from 'react';
var AMQP = require('amqplib');

const App = () => {
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');

  const handleSendMessage = async () => {
    try {
      const connection = new window.AMQP.Connection('amqp://guest:guest@192.168.192.1:5672');
      const queue = connection.declareQueue('your_queue_name');
      queue.send(message);
      setStatus('Message sent successfully!');
      connection.close();
    } catch (error) {
      console.error('Error sending message:', error.message);
      setStatus('Error sending message');
    }
  };

  return (
    <div>
      <h1>RabbitMQ Form</h1>
      <label>
        Message:
        <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
      </label>
      <button onClick={handleSendMessage}>Submit</button>
      <p>Status: {status}</p>
      {/* Include the amqp-ts library directly in your HTML file */}
      <script src="https://cdn.jsdelivr.net/npm/amqp-ts/browser/amqp-ts.min.js"></script>
    </div>
  );
};

//export default RabbitMQForm;




export default App;
